          data: [this.mediasPorTipo.pei.frequencia_media, this.mediasPorTipo.pei.rendimento_medio * 10, this.mediasPorTipo.pei.aprovacao_media, this.mediasPorTipo.pei.plataformas_media, 80 // Engajamento estimado
          ],
          borderColor: this.CORES.pei,
          backgroundColor: this.CORES.fundoPei,
          pointBackgroundColor: this.CORES.pei
        }, {
          label: 'Regular',
          data: [this.mediasPorTipo.regular.frequencia_media, this.mediasPorTipo.regular.rendimento_medio * 10, this.mediasPorTipo.regular.aprovacao_media, this.mediasPorTipo.regular.plataformas_media, 75 // Engajamento estimado
          ],
          borderColor: this.CORES.regular,
          backgroundColor: this.CORES.fundoRegular,
          pointBackgroundColor: this.CORES.regular
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          title: {
            display: true,
            text: 'Radar Comparativo'
          }
        },
        scales: {
          r: {
            angleLines: {
              display: true
            },
            suggestedMin: 0,
            suggestedMax: 100
          }
        }
      }
    };
    this.charts['radarComparativo'] = new Chart(canvas, config);
  }
  criarGraficoEvolucao() {
    const canvas = document.getElementById('graficoEvolucaoTemporal');
    if (!canvas || !this.mediasPorTipo) return;
    const config = {
      type: 'line',
      data: {
        labels: ['1Âº Bimestre', '2Âº Bimestre'],
        datasets: [{
          label: 'PEI - FrequÃªncia',
          data: [91.5, this.mediasPorTipo.pei.frequencia_media],
          borderColor: this.CORES.pei,
          backgroundColor: this.CORES.fundoPei,
          tension: 0.4
        }, {
          label: 'Regular - FrequÃªncia',
          data: [84.2, this.mediasPorTipo.regular.frequencia_media],
          borderColor: this.CORES.regular,
          backgroundColor: this.CORES.fundoRegular,
          tension: 0.4
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          title: {
            display: true,
            text: 'EvoluÃ§Ã£o da FrequÃªncia por Bimestre'
          }
        },
        scales: {
          y: {
            beginAtZero: true,
            max: 100,
            title: {
              display: true,
              text: 'FrequÃªncia (%)'
            }
          }
        }
      }
    };
    this.charts['graficoEvolucaoTemporal'] = new Chart(canvas, config);
  }
  criarGraficoDistribuicao() {
    const canvas = document.getElementById('graficoDistribuicao');
    if (!canvas || !this.mediasPorTipo) return;
    const pei = this.mediasPorTipo.pei.escolas;
    const regular = this.mediasPorTipo.regular.escolas;
    const config = {
      type: 'scatter',
      data: {
        datasets: [{
          label: 'Escolas PEI',
          data: pei.map((escola, index) => ({
            x: index + 1,
            y: escola.score_super_bi
          })),
          backgroundColor: this.CORES.pei,
          borderColor: this.CORES.pei,
          pointRadius: 8
        }, {
          label: 'Escolas Regulares',
          data: regular.map((escola, index) => ({
            x: index + 1,
            y: escola.score_super_bi
          })),
          backgroundColor: this.CORES.regular,
          borderColor: this.CORES.regular,
          pointRadius: 8
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          title: {
            display: true,
            text: 'DistribuiÃ§Ã£o de Scores por Escola'
          }
        },
        scales: {
          x: {
            title: {
              display: true,
              text: 'Escolas'
            }
          },
          y: {
            title: {
              display: true,
              text: 'Score Super BI'
            },
            min: 70,
            max: 95
          }
        }
      }
    };
    this.charts['graficoDistribuicao'] = new Chart(canvas, config);
  }
  // MÃ©todos auxiliares para o template
  formatarNumero(valor, casasDecimais = 1) {
    return valor?.toFixed(casasDecimais) || '--';
  }
  ordenarEscolas(escolas) {
    return [...escolas].sort((a, b) => b.score_super_bi - a.score_super_bi);
  }
